import React from 'react';
import styles from './Track.module.css';

const Track = (props) => {

    const renderAction = () => {
        if (props.isRemoval) {
            return (
                <button className={styles.TrackAction} onClick={props.passTrackToRemove}>
                    +
                </button>
            );
        } else {
            return (
                <button className={styles.TrackAction} onClick={props.passTrack}>
                    +
                </button>
            );
        }
    };

    const passTrack = () => {
        props.onAdd(props.track);
    };

    const removeTrack = useCallback(
        (event) => {
            props.onRemove(props.track);
        }, [props.onRemove, props.track]
    );

    return (

        <div className={styles.Track}>
            <div className={styles.TrackInfo}>
                <h3>{props.track.name}</h3>
                <p><span className={styles.Orange}>Artist:</span> {props.track.artist} | <span className={styles.Orange}>Album:</span> {props.track.album}</p>
            </div>
            {renderAction()}
        </div>
    );
};

export default Track;
